import random
random.seed(0)
BYTES = 1024
LEN = 8

def retStr8(n):
    s = str(hex(n))[2:]
    s = s[0 : len(s) - 1]
    if(len(s) < 8):
        zeros = ""
        for i in range(8 - len(s)):
            zeros = zeros + "0"
        s = zeros + s
    return s

def retStr(n):
    s = str(hex(n))[2:]
    s = s[0 : len(s) - 1]
    if(len(s) < BYTES):
        zeros = ""
        for i in range(BYTES - len(s)):
            zeros = zeros + "0"
        s = zeros + s
    return s

def out2file(n, filename):
    n_file = open(filename, 'w')
    s = retStr(n)
    for i in range(BYTES / LEN):
        n_file.write(s[(BYTES / LEN - i - 1) * LEN : (BYTES / LEN - i - 1) * LEN + LEN])
        n_file.write("\t")
            
    n_file.close()

